"use client";

import type React from "react";
import { createContext, useContext, useState } from "react";
import { useLocalStorage } from "@/modules/components/calendar/hooks";
import type { IEvent, IUser } from "@/modules/components/calendar/interfaces";
import type {
	TCalendarView,
	TEventColor,
} from "@/modules/components/calendar/types";

interface ICalendarContext {
	selectedDate: Date;
	view: TCalendarView;
	setView: (view: TCalendarView) => void;
	agendaModeGroupBy: "date" | "color";
	setAgendaModeGroupBy: (groupBy: "date" | "color") => void;
	use24HourFormat: boolean;
	toggleTimeFormat: () => void;
	setSelectedDate: (date: Date | undefined) => void;
	selectedUserId: IUser["id"] | "all";
	setSelectedUserId: (userId: IUser["id"] | "all") => void;
	badgeVariant: "dot" | "colored";
	setBadgeVariant: (variant: "dot" | "colored") => void;
	selectedColors: TEventColor[];
	filterEventsBySelectedColors: (colors: TEventColor) => void;
	filterEventsBySelectedUser: (userId: IUser["id"] | "all") => void;
	users: IUser[];
	events: IEvent[];
	addEvent: (event: IEvent) => void;
	updateEvent: (event: IEvent) => void;
	removeEvent: (eventId: number) => void;
	clearFilter: () => void;
}

interface CalendarSettings {
	badgeVariant: "dot" | "colored";
	view: TCalendarView;
	use24HourFormat: boolean;
	agendaModeGroupBy: "date" | "color";
}

const DEFAULT_SETTINGS: CalendarSettings = {
	badgeVariant: "colored",
	view: "day",
	use24HourFormat: true,
	agendaModeGroupBy: "date",
};

const CalendarContext = createContext({} as ICalendarContext);

export function CalendarProvider({
	children,
	users,
	events,
	badge = "colored",
	view = "day",
}: {
	children: React.ReactNode;
	users: IUser[];
	events: IEvent[];
	view?: TCalendarView;
	badge?: "dot" | "colored";
}) {
	const [settings, setSettings] = useLocalStorage<CalendarSettings>(
		"calendar-settings",
		{
			...DEFAULT_SETTINGS,
			badgeVariant: badge,
			view: view,
		},
	);

	const [badgeVariant, setBadgeVariantState] = useState<"dot" | "colored">(
		settings.badgeVariant,
	);
	const [currentView, setCurrentViewState] = useState<TCalendarView>(
		settings.view,
	);
	const [use24HourFormat, setUse24HourFormatState] = useState<boolean>(
		settings.use24HourFormat,
	);
	const [agendaModeGroupBy, setAgendaModeGroupByState] = useState<
		"date" | "color"
	>(settings.agendaModeGroupBy);

	const [selectedDate, setSelectedDate] = useState(new Date());
	const [selectedUserId, setSelectedUserId] = useState<IUser["id"] | "all">(
		"all",
	);
	const [selectedColors, setSelectedColors] = useState<TEventColor[]>([]);

	const [allEvents, setAllEvents] = useState<IEvent[]>(events || []);
	const [filteredEvents, setFilteredEvents] = useState<IEvent[]>(events || []);

	const updateSettings = (newPartialSettings: Partial<CalendarSettings>) => {
		setSettings({
			...settings,
			...newPartialSettings,
		});
	};

	const setBadgeVariant = (variant: "dot" | "colored") => {
		setBadgeVariantState(variant);
		updateSettings({ badgeVariant: variant });
	};

	const setView = (newView: TCalendarView) => {
		setCurrentViewState(newView);
		updateSettings({ view: newView });
	};

	const toggleTimeFormat = () => {
		const newValue = !use24HourFormat;
		setUse24HourFormatState(newValue);
		updateSettings({ use24HourFormat: newValue });
	};

	const setAgendaModeGroupBy = (groupBy: "date" | "color") => {
		setAgendaModeGroupByState(groupBy);
		updateSettings({ agendaModeGroupBy: groupBy });
	};

	const filterEventsBySelectedColors = (color: TEventColor) => {
		const isColorSelected = selectedColors.includes(color);
		const newColors = isColorSelected
			? selectedColors.filter((c) => c !== color)
			: [...selectedColors, color];

		if (newColors.length > 0) {
			const filtered = allEvents.filter((event) => {
				const eventColor = event.color || "blue";
				return newColors.includes(eventColor);
			});
			setFilteredEvents(filtered);
		} else {
			setFilteredEvents(allEvents);
		}

		setSelectedColors(newColors);
	};

	const filterEventsBySelectedUser = (userId: IUser["id"] | "all") => {
		setSelectedUserId(userId);
		if (userId === "all") {
			setFilteredEvents(allEvents);
		} else {
			const filtered = allEvents.filter((event) => event.user.id === userId);
			setFilteredEvents(filtered);
		}
	};

	const handleSelectDate = (date: Date | undefined) => {
		if (!date) return;
		setSelectedDate(date);
	};

	const addEvent = (event: IEvent) => {
		setAllEvents((prev) => [...prev, event]);
		setFilteredEvents((prev) => [...prev, event]);
	};

	const updateEvent = (event: IEvent) => {
		const updated = {
			...event,
			startDate: new Date(event.startDate).toISOString(),
			endDate: new Date(event.endDate).toISOString(),
		};

		setAllEvents((prev) => prev.map((e) => (e.id === event.id ? updated : e)));
		setFilteredEvents((prev) =>
			prev.map((e) => (e.id === event.id ? updated : e)),
		);
	};

	const removeEvent = (eventId: number) => {
		setAllEvents((prev) => prev.filter((e) => e.id !== eventId));
		setFilteredEvents((prev) => prev.filter((e) => e.id !== eventId));
	};

	const clearFilter = () => {
		setFilteredEvents(allEvents);
		setSelectedColors([]);
		setSelectedUserId("all");
	};

	const value = {
		selectedDate,
		setSelectedDate: handleSelectDate,
		selectedUserId,
		setSelectedUserId,
		badgeVariant,
		setBadgeVariant,
		users,
		selectedColors,
		filterEventsBySelectedColors,
		filterEventsBySelectedUser,
		events: filteredEvents,
		view: currentView,
		use24HourFormat,
		toggleTimeFormat,
		setView,
		agendaModeGroupBy,
		setAgendaModeGroupBy,
		addEvent,
		updateEvent,
		removeEvent,
		clearFilter,
	};

	return (
		<CalendarContext.Provider value={value}>
			{children}
		</CalendarContext.Provider>
	);
}

export function useCalendar(): ICalendarContext {
	const context = useContext(CalendarContext);
	if (!context)
		throw new Error("useCalendar must be used within a CalendarProvider.");
	return context;
}

"use client";

import React, {
	createContext,
	type ReactNode,
	useCallback,
	useContext,
	useRef,
	useState,
	useMemo,
} from "react";
import { toast } from "sonner";
import { useCalendar } from "@/modules/components/calendar/contexts/calendar-context";
import type { IEvent } from "@/modules/components/calendar/interfaces";
import { DndConfirmationDialog } from "@/modules/components/calendar/dialogs/dnd-confirmation-dialog";

interface PendingDropData {
	event: IEvent;
	newStartDate: Date;
	newEndDate: Date;
}

interface DragDropContextType {
	draggedEvent: IEvent | null;
	isDragging: boolean;
	startDrag: (event: IEvent) => void;
	endDrag: () => void;
	handleEventDrop: (date: Date, hour?: number, minute?: number) => void;
	showConfirmation: boolean;
	setShowConfirmation: (show: boolean) => void;
	pendingDropData: PendingDropData | null;
	handleConfirmDrop: () => void;
	handleCancelDrop: () => void;
}

interface DndProviderProps {
	children: ReactNode;
	showConfirmation: boolean;
}

const DragDropContext = createContext<DragDropContextType | undefined>(
	undefined,
);

export function DndProvider({
	children,
	showConfirmation: showConfirmationProp = false,
}: DndProviderProps) {
	const { updateEvent } = useCalendar();
	const [dragState, setDragState] = useState<{
		draggedEvent: IEvent | null;
		isDragging: boolean;
	}>({ draggedEvent: null, isDragging: false });

	const [showConfirmation, setShowConfirmation] =
		useState<boolean>(showConfirmationProp);

	const [pendingDropData, setPendingDropData] =
		useState<PendingDropData | null>(null);

	const onEventDroppedRef = useRef<
		((event: IEvent, newStartDate: Date, newEndDate: Date) => void) | null
	>(null);

	const startDrag = useCallback((event: IEvent) => {
		setDragState({ draggedEvent: event, isDragging: true });
	}, []);

	const endDrag = useCallback(() => {
		setDragState({ draggedEvent: null, isDragging: false });
	}, []);

	const calculateNewDates = useCallback(
		(event: IEvent, targetDate: Date, hour?: number, minute?: number) => {
			const originalStart = new Date(event.startDate);
			const originalEnd = new Date(event.endDate);
			const duration = originalEnd.getTime() - originalStart.getTime();

			const newStart = new Date(targetDate);
			if (hour !== undefined) {
				newStart.setHours(hour, minute || 0, 0, 0);
			} else {
				newStart.setHours(
					originalStart.getHours(),
					originalStart.getMinutes(),
					0,
					0,
				);
			}

			return {
				newStart,
				newEnd: new Date(newStart.getTime() + duration),
			};
		},
		[],
	);

	const isSamePosition = useCallback((date1: Date, date2: Date) => {
		return date1.getTime() === date2.getTime();
	}, []);

	const handleEventDrop = useCallback(
		(targetDate: Date, hour?: number, minute?: number) => {
			const { draggedEvent } = dragState;
			if (!draggedEvent) return;

			const { newStart, newEnd } = calculateNewDates(
				draggedEvent,
				targetDate,
				hour,
				minute,
			);
			const originalStart = new Date(draggedEvent.startDate);

			// Check if dropped in same position
			if (isSamePosition(originalStart, newStart)) {
				endDrag();
				return;
			}

			if (showConfirmation) {
				// Show confirmation dialog if user wants it
				setPendingDropData({
					event: draggedEvent,
					newStartDate: newStart,
					newEndDate: newEnd,
				});
			} else {
				// Instantly update event if user doesn't want confirmation
				const callback = onEventDroppedRef.current;
				if (callback) {
					callback(draggedEvent, newStart, newEnd);
				}
				endDrag();
			}
		},
		[dragState, calculateNewDates, isSamePosition, endDrag, showConfirmation],
	);

	const handleConfirmDrop = useCallback(() => {
		if (!pendingDropData) return;

		const callback = onEventDroppedRef.current;
		if (callback) {
			callback(
				pendingDropData.event,
				pendingDropData.newStartDate,
				pendingDropData.newEndDate,
			);
		}

		// Reset states
		setPendingDropData(null);
		endDrag();
	}, [pendingDropData, endDrag]);

	const handleCancelDrop = useCallback(() => {
		setPendingDropData(null);
		endDrag();
	}, [endDrag]);

	// Default event update handler
	const handleEventUpdate = useCallback(
		(event: IEvent, newStartDate: Date, newEndDate: Date) => {
			try {
				const updatedEvent = {
					...event,
					startDate: newStartDate.toISOString(),
					endDate: newEndDate.toISOString(),
				};
				updateEvent(updatedEvent);
				toast.success("Event updated successfully");
			} catch {
				toast.error("Failed to update event");
			}
		},
		[updateEvent],
	);

	// Set default callback
	React.useEffect(() => {
		onEventDroppedRef.current = handleEventUpdate;
	}, [handleEventUpdate]);

	// When the prop changes, update the state
	React.useEffect(() => {
		setShowConfirmation(showConfirmationProp);
	}, [showConfirmationProp]);

	const contextValue = useMemo(
		() => ({
			draggedEvent: dragState.draggedEvent,
			isDragging: dragState.isDragging,
			startDrag,
			endDrag,
			handleEventDrop,
			showConfirmation,
			pendingDropData,
			handleConfirmDrop,
			handleCancelDrop,
			setShowConfirmation,
		}),
		[
			dragState,
			showConfirmation,
			pendingDropData,
			startDrag,
			endDrag,
			handleEventDrop,
			handleConfirmDrop,
			handleCancelDrop,
			setShowConfirmation,
		],
	);

	return (
		<DragDropContext.Provider value={contextValue}>
			{showConfirmation && pendingDropData && <DndConfirmationDialog />}
			{children}
		</DragDropContext.Provider>
	);
}

export function useDragDrop() {
	const context = useContext(DragDropContext);
	if (!context) {
		throw new Error("useDragDrop must be used within a DragDropProvider");
	}
	return context;
}

import { zodResolver } from "@hookform/resolvers/zod";
import { addMinutes, format, set } from "date-fns";
import { type ReactNode, useEffect, useMemo } from "react";
import { useForm } from "react-hook-form";
import { toast } from "sonner";
import { Button } from "@/components/ui/button";
import { DateTimePicker } from "@/components/ui/date-time-picker";
import {
	Form,
	FormControl,
	FormField,
	FormItem,
	FormLabel,
	FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import {
	Modal,
	ModalClose,
	ModalContent,
	ModalDescription,
	ModalFooter,
	ModalHeader,
	ModalTitle,
	ModalTrigger,
} from "@/components/ui/responsive-modal";
import {
	Select,
	SelectContent,
	SelectItem,
	SelectTrigger,
	SelectValue,
} from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { COLORS } from "@/modules/components/calendar/constants";
import { useCalendar } from "@/modules/components/calendar/contexts/calendar-context";
import { useDisclosure } from "@/modules/components/calendar/hooks";
import type { IEvent } from "@/modules/components/calendar/interfaces";
import {
	eventSchema,
	type TEventFormData,
} from "@/modules/components/calendar/schemas";

interface IProps {
	children: ReactNode;
	startDate?: Date;
	startTime?: { hour: number; minute: number };
	event?: IEvent;
}

export function AddEditEventDialog({
	children,
	startDate,
	startTime,
	event,
}: IProps) {
	const { isOpen, onClose, onToggle } = useDisclosure();
	const { addEvent, updateEvent } = useCalendar();
	const isEditing = !!event;

	const initialDates = useMemo(() => {
		if (!isEditing && !event) {
			if (!startDate) {
				const now = new Date();
				return { startDate: now, endDate: addMinutes(now, 30) };
			}
			const start = startTime
				? set(new Date(startDate), {
						hours: startTime.hour,
						minutes: startTime.minute,
						seconds: 0,
					})
				: new Date(startDate);
			const end = addMinutes(start, 30);
			return { startDate: start, endDate: end };
		}

		return {
			startDate: new Date(event.startDate),
			endDate: new Date(event.endDate),
		};
	}, [startDate, startTime, event, isEditing]);

	const form = useForm<TEventFormData>({
		resolver: zodResolver(eventSchema),
		defaultValues: {
			title: event?.title ?? "",
			description: event?.description ?? "",
			startDate: initialDates.startDate,
			endDate: initialDates.endDate,
			color: event?.color ?? "blue",
		},
	});

	useEffect(() => {
		form.reset({
			title: event?.title ?? "",
			description: event?.description ?? "",
			startDate: initialDates.startDate,
			endDate: initialDates.endDate,
			color: event?.color ?? "blue",
		});
	}, [event, initialDates, form]);

	const onSubmit = (values: TEventFormData) => {
		try {
			const formattedEvent: IEvent = {
				...values,
				startDate: format(values.startDate, "yyyy-MM-dd'T'HH:mm:ss"),
				endDate: format(values.endDate, "yyyy-MM-dd'T'HH:mm:ss"),
				id: isEditing ? event.id : Math.floor(Math.random() * 1000000),
				user: isEditing
					? event.user
					: {
							id: Math.floor(Math.random() * 1000000).toString(),
							name: "Jeraidi Yassir",
							picturePath: null,
						},
				color: values.color,
			};

			if (isEditing) {
				updateEvent(formattedEvent);
				toast.success("Event updated successfully");
			} else {
				addEvent(formattedEvent);
				toast.success("Event created successfully");
			}

			onClose();
			form.reset();
		} catch (error) {
			console.error(`Error ${isEditing ? "editing" : "adding"} event:`, error);
			toast.error(`Failed to ${isEditing ? "edit" : "add"} event`);
		}
	};

	return (
		<Modal open={isOpen} onOpenChange={onToggle} modal={false}>
			<ModalTrigger asChild>{children}</ModalTrigger>
			<ModalContent>
				<ModalHeader>
					<ModalTitle>{isEditing ? "Edit Event" : "Add New Event"}</ModalTitle>
					<ModalDescription>
						{isEditing
							? "Modify your existing event."
							: "Create a new event for your calendar."}
					</ModalDescription>
				</ModalHeader>

				<Form {...form}>
					<form
						id="event-form"
						onSubmit={form.handleSubmit(onSubmit)}
						className="grid gap-4 py-4"
					>
						<FormField
							control={form.control}
							name="title"
							render={({ field, fieldState }) => (
								<FormItem>
									<FormLabel htmlFor="title" className="required">
										Title
									</FormLabel>
									<FormControl>
										<Input
											id="title"
											placeholder="Enter a title"
											{...field}
											className={fieldState.invalid ? "border-red-500" : ""}
										/>
									</FormControl>
									<FormMessage />
								</FormItem>
							)}
						/>
						<FormField
							control={form.control}
							name="startDate"
							render={({ field }) => (
								<DateTimePicker form={form} field={field} />
							)}
						/>
						<FormField
							control={form.control}
							name="endDate"
							render={({ field }) => (
								<DateTimePicker form={form} field={field} />
							)}
						/>
						<FormField
							control={form.control}
							name="color"
							render={({ field, fieldState }) => (
								<FormItem>
									<FormLabel className="required">Variant</FormLabel>
									<FormControl>
										<Select value={field.value} onValueChange={field.onChange}>
											<SelectTrigger
												className={`w-full ${
													fieldState.invalid ? "border-red-500" : ""
												}`}
											>
												<SelectValue placeholder="Select a variant" />
											</SelectTrigger>
											<SelectContent>
												{COLORS.map((color) => (
													<SelectItem value={color} key={color}>
														<div className="flex items-center gap-2">
															<div
																className={`size-3.5 rounded-full bg-${color}-600 dark:bg-${color}-700`}
															/>
															{color}
														</div>
													</SelectItem>
												))}
											</SelectContent>
										</Select>
									</FormControl>
									<FormMessage />
								</FormItem>
							)}
						/>
						<FormField
							control={form.control}
							name="description"
							render={({ field, fieldState }) => (
								<FormItem>
									<FormLabel className="required">Description</FormLabel>
									<FormControl>
										<Textarea
											{...field}
											placeholder="Enter a description"
											className={fieldState.invalid ? "border-red-500" : ""}
										/>
									</FormControl>
									<FormMessage />
								</FormItem>
							)}
						/>
					</form>
				</Form>
				<ModalFooter className="flex justify-end gap-2">
					<ModalClose asChild>
						<Button type="button" variant="outline">
							Cancel
						</Button>
					</ModalClose>
					<Button form="event-form" type="submit">
						{isEditing ? "Save Changes" : "Create Event"}
					</Button>
				</ModalFooter>
			</ModalContent>
		</Modal>
	);
}

import { TrashIcon } from "lucide-react";
import { toast } from "sonner";
import {
	AlertDialog,
	AlertDialogAction,
	AlertDialogCancel,
	AlertDialogContent,
	AlertDialogDescription,
	AlertDialogFooter,
	AlertDialogHeader,
	AlertDialogTitle,
	AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { Button } from "@/components/ui/button";
import { useCalendar } from "@/modules/components/calendar/contexts/calendar-context";

interface DeleteEventDialogProps {
	eventId: number;
}

export default function DeleteEventDialog({ eventId }: DeleteEventDialogProps) {
	const { removeEvent } = useCalendar();

	const deleteEvent = () => {
		try {
			removeEvent(eventId);
			toast.success("Event deleted successfully.");
		} catch {
			toast.error("Error deleting event.");
		}
	};

	if (!eventId) {
		return null;
	}

	return (
		<AlertDialog>
			<AlertDialogTrigger asChild>
				<Button variant="destructive">
					<TrashIcon />
					Delete
				</Button>
			</AlertDialogTrigger>
			<AlertDialogContent>
				<AlertDialogHeader>
					<AlertDialogTitle>Are you absolutely sure?</AlertDialogTitle>
					<AlertDialogDescription>
						This action cannot be undone. This will permanently delete your
						event and remove event data from our servers.
					</AlertDialogDescription>
				</AlertDialogHeader>
				<AlertDialogFooter>
					<AlertDialogCancel>Cancel</AlertDialogCancel>
					<AlertDialogAction onClick={deleteEvent}>Continue</AlertDialogAction>
				</AlertDialogFooter>
			</AlertDialogContent>
		</AlertDialog>
	);
}

import { useDragDrop } from "@/modules/components/calendar/contexts/dnd-context";
import { EventDropConfirmationDialog } from "./event-drop-confirmation-dialog";
import { memo } from "react";

const DndConfirmationDialog = memo(() => {
	const {
		showConfirmation,
		pendingDropData,
		handleConfirmDrop,
		handleCancelDrop,
	} = useDragDrop();

	if (!showConfirmation || !pendingDropData) return null;

	return (
		<EventDropConfirmationDialog
			open={showConfirmation}
			onOpenChange={() => {}} // Controlled by context
			event={pendingDropData.event}
			newStartDate={pendingDropData.newStartDate}
			newEndDate={pendingDropData.newEndDate}
			onConfirm={handleConfirmDrop}
			onCancel={handleCancelDrop}
		/>
	);
});

DndConfirmationDialog.displayName = "DndConfirmationDialog";

export { DndConfirmationDialog };

"use client";

import { format, parseISO } from "date-fns";
import { Calendar, Clock, Text, User } from "lucide-react";
import type { ReactNode } from "react";
import { toast } from "sonner";
import { Button } from "@/components/ui/button";
import {
	Dialog,
	DialogClose,
	DialogContent,
	DialogHeader,
	DialogTitle,
	DialogTrigger,
} from "@/components/ui/dialog";
import { ScrollArea } from "@/components/ui/scroll-area";
import { useCalendar } from "@/modules/components/calendar/contexts/calendar-context";
import { AddEditEventDialog } from "@/modules/components/calendar/dialogs/add-edit-event-dialog";
import { formatTime } from "@/modules/components/calendar/helpers";
import type { IEvent } from "@/modules/components/calendar/interfaces";

interface IProps {
	event: IEvent;
	children: ReactNode;
}

export function EventDetailsDialog({ event, children }: IProps) {
	const startDate = parseISO(event.startDate);
	const endDate = parseISO(event.endDate);
	const { use24HourFormat, removeEvent } = useCalendar();

	const deleteEvent = (eventId: number) => {
		try {
			removeEvent(eventId);
			toast.success("Event deleted successfully.");
		} catch {
			toast.error("Error deleting event.");
		}
	};

	return (
		<Dialog>
			<DialogTrigger asChild>{children}</DialogTrigger>
			<DialogContent>
				<DialogHeader>
					<DialogTitle>{event.title}</DialogTitle>
				</DialogHeader>

				<ScrollArea className="max-h-[80vh]">
