export function EventBlock({ event, className }: IProps) {
	const { badgeVariant, use24HourFormat } = useCalendar();

	const start = parseISO(event.startDate);
	const end = parseISO(event.endDate);
	const durationInMinutes = differenceInMinutes(end, start);
	const heightInPixels = (durationInMinutes / 60) * 96 - 8;

	const color = (
		badgeVariant === "dot" ? `${event.color}-dot` : event.color
	) as VariantProps<typeof calendarWeekEventCardVariants>["color"];

	const calendarWeekEventCardClasses = cn(
		calendarWeekEventCardVariants({ color, className }),
		durationInMinutes < 35 && "py-0 justify-center",
	);

	return (
		<ResizableEvent event={event}>
			<DraggableEvent event={event}>
				<EventDetailsDialog event={event}>
					<div
						role="button"
						tabIndex={0}
						className={calendarWeekEventCardClasses}
						style={{ height: `${heightInPixels}px` }}
					>
						<div className="flex items-center gap-1.5 truncate">
							{badgeVariant === "dot" && (
								<svg
									width="8"
									height="8"
									viewBox="0 0 8 8"
									className="shrink-0"
								>
									<circle cx="4" cy="4" r="4" />
								</svg>
							)}

							<p className="truncate font-semibold">{event.title}</p>
						</div>

						{durationInMinutes > 25 && (
							<p>
								{formatTime(start, use24HourFormat)} -{" "}
								{formatTime(end, use24HourFormat)}
							</p>
						)}
					</div>
				</EventDetailsDialog>
			</DraggableEvent>
		</ResizableEvent>
	);
}

import { areIntervalsOverlapping, parseISO } from "date-fns";
import { getEventBlockStyle } from "@/modules/components/calendar/helpers";
import type { IEvent } from "@/modules/components/calendar/interfaces";
import { EventBlock } from "@/modules/components/calendar/views/week-and-day-view/event-block";

interface RenderGroupedEventsProps {
	groupedEvents: IEvent[][];
	day: Date;
}

export function RenderGroupedEvents({
	groupedEvents,
	day,
}: RenderGroupedEventsProps) {
	return groupedEvents.map((group, groupIndex) =>
		group.map((event) => {
			let style = getEventBlockStyle(
				event,
				day,
				groupIndex,
				groupedEvents.length,
			);
			const hasOverlap = groupedEvents.some(
				(otherGroup, otherIndex) =>
					otherIndex !== groupIndex &&
					otherGroup.some((otherEvent) =>
						areIntervalsOverlapping(
							{
								start: parseISO(event.startDate),
								end: parseISO(event.endDate),
							},
							{
								start: parseISO(otherEvent.startDate),
								end: parseISO(otherEvent.endDate),
							},
						),
					),
			);

			if (!hasOverlap) style = { ...style, width: "100%", left: "0%" };

			return (
				<div key={event.id} className="absolute p-1" style={style}>
					<EventBlock event={event} />
				</div>
			);
		}),
	);
}

import {
	addDays,
	differenceInDays,
	endOfWeek,
	isAfter,
	isBefore,
	parseISO,
	startOfDay,
	startOfWeek,
} from "date-fns";
import { useMemo } from "react";
import type { IEvent } from "@/modules/components/calendar/interfaces";
import { MonthEventBadge } from "@/modules/components/calendar/views/month-view/month-event-badge";

interface IProps {
	selectedDate: Date;
	multiDayEvents: IEvent[];
}

export function WeekViewMultiDayEventsRow({
	selectedDate,
	multiDayEvents,
}: IProps) {
	const weekStart = startOfWeek(selectedDate);
	const weekEnd = endOfWeek(selectedDate);
	const weekDays = Array.from({ length: 7 }, (_, i) => addDays(weekStart, i));

	const processedEvents = useMemo(() => {
		return multiDayEvents
			.map((event) => {
				const start = parseISO(event.startDate);
				const end = parseISO(event.endDate);
				const adjustedStart = isBefore(start, weekStart) ? weekStart : start;
				const adjustedEnd = isAfter(end, weekEnd) ? weekEnd : end;
				const startIndex = differenceInDays(adjustedStart, weekStart);
				const endIndex = differenceInDays(adjustedEnd, weekStart);

				return {
					...event,
					adjustedStart,
					adjustedEnd,
					startIndex,
					endIndex,
				};
			})
			.sort((a, b) => {
				const startDiff = a.adjustedStart.getTime() - b.adjustedStart.getTime();
				if (startDiff !== 0) return startDiff;
				return b.endIndex - b.startIndex - (a.endIndex - a.startIndex);
			});
	}, [multiDayEvents, weekStart, weekEnd]);

	const eventRows = useMemo(() => {
		const rows: (typeof processedEvents)[] = [];

		processedEvents.forEach((event) => {
			let rowIndex = rows.findIndex((row) =>
				row.every(
					(e) => e.endIndex < event.startIndex || e.startIndex > event.endIndex,
				),
			);

			if (rowIndex === -1) {
				rowIndex = rows.length;
				rows.push([]);
			}

			rows[rowIndex].push(event);
		});

		return rows;
	}, [processedEvents]);

	const hasEventsInWeek = useMemo(() => {
		return multiDayEvents.some((event) => {
			const start = parseISO(event.startDate);
			const end = parseISO(event.endDate);

			return (
				// Event starts within the week
				(start >= weekStart && start <= weekEnd) ||
				// Event ends within the week
				(end >= weekStart && end <= weekEnd) ||
				// Event spans the entire week
				(start <= weekStart && end >= weekEnd)
			);
		});
	}, [multiDayEvents, weekStart, weekEnd]);

	if (!hasEventsInWeek) {
		return null;
	}

	return (
		<div className="overflow-hidden flex">
			<div className="w-18 border-b"></div>
			<div className="grid flex-1 grid-cols-7 divide-x border-b border-l">
				{weekDays.map((day, dayIndex) => (
					<div
						key={day.toISOString()}
						className="flex h-full flex-col gap-1 py-1"
					>
						{eventRows.map((row, rowIndex) => {
							const event = row.find(
								(e) => e.startIndex <= dayIndex && e.endIndex >= dayIndex,
							);

							if (!event) {
								return (
									<div key={`${rowIndex}-${dayIndex}`} className="h-6.5" />
								);
							}

							let position: "first" | "middle" | "last" | "none" = "none";

							if (
								dayIndex === event.startIndex &&
								dayIndex === event.endIndex
							) {
								position = "none";
							} else if (dayIndex === event.startIndex) {
								position = "first";
							} else if (dayIndex === event.endIndex) {
								position = "last";
							} else {
								position = "middle";
							}

							return (
								<MonthEventBadge
									key={`${event.id}-${dayIndex}`}
									event={event}
									cellDate={startOfDay(day)}
									position={position}
								/>
							);
						})}
					</div>
				))}
			</div>
		</div>
	);
}

import { getYear, isSameDay, isSameMonth } from "date-fns";
import { motion } from "framer-motion";
import { cn } from "@/lib/utils";
import {
	staggerContainer,
	transition,
} from "@/modules/components/calendar/animations";
import { useCalendar } from "@/modules/components/calendar/contexts/calendar-context";
import { EventListDialog } from "@/modules/components/calendar/dialogs/events-list-dialog";
import { getCalendarCells } from "@/modules/components/calendar/helpers";
import type { IEvent } from "@/modules/components/calendar/interfaces";
import { EventBullet } from "@/modules/components/calendar/views/month-view/event-bullet";

interface IProps {
	singleDayEvents: IEvent[];
	multiDayEvents: IEvent[];
}

const MONTHS = [
	"January",
	"February",
	"March",
	"April",
	"May",
	"June",
	"July",
	"August",
	"September",
	"October",
	"November",
	"December",
];

const WEEKDAYS = ["Su", "Mo", "Tu", "We", "Th", "Fr", "Sa"];

export function CalendarYearView({ singleDayEvents, multiDayEvents }: IProps) {
	const { selectedDate, setSelectedDate } = useCalendar();
	const currentYear = getYear(selectedDate);
	const allEvents = [...multiDayEvents, ...singleDayEvents];

	return (
		<div className="flex flex-col h-full  overflow-y-auto p-4  sm:p-6">
			{/* Year grid */}
			<motion.div
				initial="initial"
				animate="animate"
				variants={staggerContainer}
				className="grid grid-cols-1 gap-4 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 auto-rows-fr"
			>
				{MONTHS.map((month, monthIndex) => {
					const monthDate = new Date(currentYear, monthIndex, 1);
					const cells = getCalendarCells(monthDate);

					return (
						<motion.div
							key={month}
							className="flex flex-col border border-border rounded-lg shadow-sm overflow-hidden"
							initial={{ opacity: 0, scale: 0.95 }}
							animate={{ opacity: 1, scale: 1 }}
							transition={{ delay: monthIndex * 0.05, ...transition }}
							role="region"
							aria-label={`${month} ${currentYear} calendar`}
						>
							{/* Month header */}
							<div
								className="px-3 py-2 text-center font-semibold text-sm sm:text-base cursor-pointer hover:bg-primary/20 transition-colors"
								onClick={() =>
									setSelectedDate(new Date(currentYear, monthIndex, 1))
								}
								role="button"
								tabIndex={0}
								onKeyDown={(e) => {
									if (e.key === "Enter" || e.key === " ") {
										setSelectedDate(new Date(currentYear, monthIndex, 1));
									}
								}}
								aria-label={`Select ${month}`}
							>
								{month}
							</div>

							<div className="grid grid-cols-7 text-center text-xs font-medium text-muted-foreground py-2">
								{WEEKDAYS.map((day) => (
									<div key={day} className="p-1">
										{day}
									</div>
								))}
							</div>

							<div className="grid grid-cols-7 gap-0.5 p-1.5 flex-grow text-xs">
								{cells.map((cell) => {
									const isCurrentMonth = isSameMonth(cell.date, monthDate);
									const isToday = isSameDay(cell.date, new Date());
									const dayEvents = allEvents.filter((event) =>
										isSameDay(new Date(event.startDate), cell.date),
									);
									const hasEvents = dayEvents.length > 0;

									return (
										<div
											key={cell.date.toISOString()}
											className={cn(
												"flex flex-col items-center justify-start p-1 min-h-[2rem] relative",
												!isCurrentMonth && "text-muted-foreground/40",
												hasEvents && isCurrentMonth
													? "cursor-pointer hover:bg-accent/20 hover:rounded-md"
													: "cursor-default",
											)}
										>
											{isCurrentMonth && hasEvents ? (
												<EventListDialog date={cell.date} events={dayEvents}>
													<div className="w-full h-full flex flex-col items-center justify-start gap-0.5">
														<span
															className={cn(
																"size-5 flex items-center justify-center font-medium",
																isToday &&
																	"rounded-full bg-primary text-primary-foreground",
															)}
														>
															{cell.day}
														</span>
														<div className="flex justify-center items-center gap-0.5">
															{dayEvents.length <= 2 ? (
																dayEvents
																	.slice(0, 2)
																	.map((event) => (
																		<EventBullet
																			key={event.id}
																			color={event.color}
																			className="size-1.5"
																		/>
																	))
															) : (
																<div className="flex flex-col justify-center items-center">
																	<EventBullet
																		color={dayEvents[0].color}
																		className="size-1.5"
																	/>
																	<span className="text-[0.6rem]">
																		+{dayEvents.length - 1}
																	</span>
																</div>
															)}
														</div>
													</div>
												</EventListDialog>
											) : (
												<div className="w-full h-full flex flex-col items-center justify-start">
													<span
														className={cn(
															"size-5 flex items-center justify-center font-medium",
														)}
													>
														{cell.day}
													</span>
												</div>
											)}
										</div>
									);
								})}
							</div>
						</motion.div>
					);
				})}
			</motion.div>
		</div>
	);
}

